/**
 * Apply RLS Policies and Enable Real-time
 * 
 * This script applies RLS policies and enables real-time replication
 * by executing SQL directly via Supabase REST API (requires service role key)
 */

import { createClient } from '@supabase/supabase-js';
import * as dotenv from 'dotenv';
import * as path from 'path';
import * as fs from 'fs';

dotenv.config({ path: path.join(__dirname, '../.env') });

const supabaseUrl = process.env.SUPABASE_URL;
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY || process.env.SUPABASE_ANON_KEY;

if (!supabaseUrl || !supabaseServiceKey) {
  console.error('âŒ Error: SUPABASE_URL and SUPABASE_SERVICE_ROLE_KEY or SUPABASE_ANON_KEY required');
  process.exit(1);
}

const supabase = createClient(supabaseUrl, supabaseServiceKey);

async function applyRLS() {
  console.log('ğŸ”’ Applying Row Level Security (RLS) policies...\n');
  
  const rlsSQLPath = path.join(__dirname, 'setup-rls-policies-direct.sql');
  if (!fs.existsSync(rlsSQLPath)) {
    console.error(`âŒ RLS SQL file not found: ${rlsSQLPath}`);
    return false;
  }

  const rlsSQL = fs.readFileSync(rlsSQLPath, 'utf-8');
  
  // Split SQL into individual statements
  const statements = rlsSQL
    .split(';')
    .map(s => s.trim())
    .filter(s => s.length > 0 && !s.startsWith('--'));

  console.log(`ğŸ“‹ Executing ${statements.length} SQL statements...\n`);

  for (const statement of statements) {
    try {
      // Use REST API to execute SQL (requires service role key)
      const { data, error } = await supabase.rpc('exec_sql', { 
        sql: statement + ';' 
      });

      if (error) {
        // If exec_sql doesn't exist, we need to use direct database connection
        if (error.message.includes('function') || error.message.includes('not found')) {
          console.log('âš ï¸  Direct SQL execution not available via REST API');
          console.log('ğŸ’¡ Please run the SQL manually in Supabase Dashboard â†’ SQL Editor');
          console.log(`   File: ${rlsSQLPath}\n`);
          return false;
        }
        
        // Ignore "already exists" errors
        if (!error.message.includes('already exists') && !error.message.includes('does not exist')) {
          console.warn(`âš ï¸  Warning: ${error.message}`);
        }
      }
    } catch (err: any) {
      console.warn(`âš ï¸  Error executing statement: ${err.message}`);
    }
  }

  console.log('âœ… RLS policies applied!\n');
  return true;
}

async function enableRealtime() {
  console.log('ğŸ“¡ Enabling real-time replication...\n');
  
  const realtimeSQLPath = path.join(__dirname, 'enable-realtime-direct.sql');
  if (!fs.existsSync(realtimeSQLPath)) {
    console.error(`âŒ Real-time SQL file not found: ${realtimeSQLPath}`);
    return false;
  }

  const realtimeSQL = fs.readFileSync(realtimeSQLPath, 'utf-8');
  
  // Split SQL into individual statements
  const statements = realtimeSQL
    .split(';')
    .map(s => s.trim())
    .filter(s => s.length > 0 && !s.startsWith('--') && !s.startsWith('SELECT'));

  console.log(`ğŸ“‹ Executing ${statements.length} SQL statements...\n`);

  for (const statement of statements) {
    try {
      const { data, error } = await supabase.rpc('exec_sql', { 
        sql: statement + ';' 
      });

      if (error) {
        if (error.message.includes('function') || error.message.includes('not found')) {
          console.log('âš ï¸  Direct SQL execution not available via REST API');
          console.log('ğŸ’¡ Please enable real-time manually in Supabase Dashboard â†’ Database â†’ Replication');
          console.log(`   OR run SQL manually: ${realtimeSQLPath}\n`);
          return false;
        }
        
        if (!error.message.includes('already exists') && !error.message.includes('does not exist')) {
          console.warn(`âš ï¸  Warning: ${error.message}`);
        }
      }
    } catch (err: any) {
      console.warn(`âš ï¸  Error executing statement: ${err.message}`);
    }
  }

  console.log('âœ… Real-time enabled!\n');
  return true;
}

